			this.length = 0;
			this.push(...normalizedConfigs.map(this[ConfigArraySymbol.preprocessConfig].bind(this)));
			this.forEach(assertValidFilesAndIgnores);
			this[ConfigArraySymbol.isNormalized] = true;

